	IF NEW.subject_type = 0 THEN
    	UPDATE device SET price = (1-NEW.discount)*original_price WHERE NEW.subject = id;
    ELSEIF NEW.subject_type = 1 THEN
    	UPDATE smartlife SET price = (1-NEW.discount)*original_price WHERE NEW.subject = id;
    END IF;
END
